import React, { Component } from 'react'
import { Button } from 'antd';
import 'antd/dist/antd.css';
import { post } from '../../../../../../utils/axios';

export default class Tr extends Component {
    delete = async() =>{
        if(prompt("确定删除吗？")){
            try{
                let res = await post('/api/delete',{
                    number:''
                })
                console.log(res)
                }
            catch{

            }   
        }
    }
    render() {
        const {number,date,thing,name,student_number,status,address} = this.props
        return (
                <tr className="tdwrp">
                    <td><input type="checkbox" /></td>
                    <td>{number}</td>
                    <td>{date}</td>
                    <td>{thing}</td>
                    <td>{name}</td>
                    <td>{student_number}</td>
                    <td>{status}</td>
                    <td>找回日期</td>
                    <td>{address}</td>
                    <td>
                        <div>
                            <Button>编辑</Button>
                            <Button type="primary" danger onClick={this.delete}>删除</Button>                       
                        </div>
                    </td>
                </tr>
        )
    }
}
