import React, { Component } from 'react'
import { Button,Input } from 'antd';
import 'antd/dist/antd.css';
import { post } from '../../../../../../utils/axios';

export default class Tr extends Component {
    state={
        isedi:false,
        ...this.props
    }
    edi=()=>{
        this.setState({isedi:true})
    }
    chagevalue=()=>{}
    delete = async(e) =>{
            try{
                let res = await post('http://1.14.74.79:9090/laf/delete',{
                    number:Number(e.currentTarget.value)
                })
                console.log(res)
                this.props.getTrmsg()
                }
            catch(err){
                console.log(err)
            }   
        }
        savedate=(e)=>{
            console.log(e.currentTarget.value)
        }
    update=async(e)=>{
        console.log(e.currentTarget.value)
        // try{
        //     let res = await post('http://1.14.74.79:9090/laf/update',{
        //             date:'',
        //             re_date:'',
        //             thing:'',
        //             name:'',
        //             student_number:'',
        //             address:'',
        //             status:'',
        //             number:Number(e.currentTarget.value)
        //     })
        //     console.log(res)
        // }
        // catch(err){

        // }
    }
    cancel=()=>{
        this.setState({isedi:false})
    }
    render() {
        const {number,date,thing,name,student_number,status,address,re_date} = this.props
        return (
            this.state.isedi ?
                    <tr className="tdwrp">
                        <td><input type="checkbox" /></td>
                        <td>{number}</td>
                        <td><Input defaultValue={date} onChange={this.savedate}/></td>
                        <td><Input defaultValue={thing} onChange={this.savething}/></td>
                        <td><Input defaultValue={name} onChange={this.savedate}/></td>
                        <td><Input defaultValue={student_number} onChange={this.savedate}/></td>
                        <td><Input defaultValue={status} onChange={this.savedate}/></td>
                        <td><Input defaultValue={re_date} onChange={this.savedate}/></td>
                        <td><Input defaultValue={address} onChange={this.savedate}/></td>
                        <td>
                            <div>
                                <Button onClick={this.update} value={number}>保存</Button>
                                <Button onClick={this.cancel}>取消</Button>                    
                            </div>
                        </td>
                </tr>:
                <tr className="tdwrp">
                    <td><input type="checkbox" /></td>
                    <td>{number}</td>
                    <td>{date}</td>
                    <td>{thing}</td>
                    <td>{name}</td>
                    <td>{student_number}</td>
                    <td>{status ? '已找回' : '未找回'}</td>
                    <td>{re_date}</td>
                    <td>{address}</td>
                    <td>
                        <div>
                            <Button onClick={this.edi}>编辑</Button>
                            <Button type="primary" danger onClick={this.delete} value={number}>删除</Button>                    
                        </div>
                    </td>
                </tr>
        )
    }
}
