import React, { Component } from 'react'
import axios from 'axios';
import { Input } from 'antd';
import 'antd/dist/antd.css';
import { UserOutlined,LockOutlined,UserAddOutlined } from '@ant-design/icons'
import './index.css'

export default class Register extends Component {
    state={
        isnumhave:true,
        isRegitrationpasswordhave:true,
        isusernamehave:true,
        isnum:true,
        isRegitrationpassword:true,
        isusername:true
    }
    saveRegistrationstudentId=(e)=>{
        this.setState({RegistrationId:e.currentTarget.value})
    }
    saveRegistrationpassword=(e)=>{
        this.setState({saveRegistrationpassword:e.currentTarget.value})
    }
    saveRegistrationName=(e)=>{
        this.setState({RegistrationName:e.currentTarget.value})
    }
    Registration=()=>{
        const {RegistrationstudentId,Registrationpassword,RegistrationName} = this.state
        if(RegistrationstudentId && Registrationpassword && RegistrationName ){
        axios({
            method:'post',
            url:'/api/user/signup',
            params:{
                user_name:RegistrationName,
                password:Registrationpassword,
                student_number:Number(RegistrationstudentId)
            }
        })
    }}
    render() {
        return (
            <div id="Registerwrp">
                <br />
                <Input size='middle' onChange={this.saveRegistrationstudentId} className="int1" placeholder="请输入学号" prefix={<UserOutlined />} />
                {this.state.isnumhave ? (this.state.isid ? <br></br> : <div className="tip1">长度为10位！</div>) : <div className="tip1">请输入学号！</div>}
                <Input.Password size='middle' onChange={this.saveRegistrationpassword} className="int1" prefix={<LockOutlined />} placeholder="请输入密码"/>
                <br />
                <Input.Password size='middle' onChange={this.saveRegistrationpassword} className="int1" prefix={<LockOutlined />} placeholder="请再次输入密码"/>
                <br />
                <Input className="int1" size='middle' onChange={this.saveRegistrationphone} placeholder="请输入用户名" prefix={<UserAddOutlined />} />
                <br />
                <button onClick={this.Registration} className="Registerbtn">注册</button>
            </div>
        )
    }
}
