import React, { Component } from 'react'
import { NavLink } from 'react-router-dom'
import { Input } from 'antd';
import { UserOutlined,LockOutlined } from '@ant-design/icons'
import './index.css'

export default class Login extends Component {
    state={
        isid:false,
        ispassword:false
    }
    saveStudentId=(e)=>{
        let regid = /^[0-9]{10}$/
        this.setState({isid:regid.test(e.currentTarget.value)})
        console.log(regid.test(e.currentTarget.value))
    }
    savePassword=(e)=>{
        let regpassword = /[*]{8}/
        this.setState({ispassword:regpassword.test(e.currentTarget.value)})
    }
    render() {
        return (
            <div id="Loginwrp">
                    <Input className="int" onChange={this.saveStudentId} placeholder="请输入学号" prefix={<UserOutlined />} />
                    {this.state.isid ? null : <div >长度为十</div>}
                    <Input.Password className="int" onChange={this.savePassword} prefix={<LockOutlined />} placeholder="请输入密码"/>
                    <NavLink className="noaccount" to="/Register">没有账号点击注册</NavLink>
                <div className="checkbox"><input type="checkbox" />记住密码</div>
                <button className="Loginbtn">登录</button>
            </div>
        )
    }
}
