import React, { useState } from 'react';
import {Form,Input,Button,DatePicker} from 'antd';
import './index.css'

const FormSizeDemo = (props) => {
  const [componentSize, setComponentSize] = useState('default');
  let [state,setState] = useState('in')

  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  return (
    <>
      <Form
        labelCol={{
          span: 4,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="horizontal"
        initialValues={{
          size: componentSize,
        }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
        className="From"
      >
        <Form.Item label="失物名称">
          <Input placeholder="(默认为一卡通，点击修改)" onChange={props.getnewadd}/>
        </Form.Item>
        <Form.Item label="失主姓名">
          <Input placeholder="点击输入姓名"/>
        </Form.Item>
        <Form.Item label="失主学号">
          <Input placeholder="点击输入学号信息"/>
        </Form.Item>
        <Form.Item label="选择日期">
          <DatePicker />
        </Form.Item>
        <Form.Item label="领取地址">
          <Input placeholder="点击输入地址"/>
        </Form.Item>
        <Form.Item className="formbutton">
          <Button type="primary">立即创建</Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default FormSizeDemo
