import React, { Component } from 'react'
import { Button } from 'antd';
import 'antd/dist/antd.css';
import { post } from '../../../../../../utils/axios';

export default class Tr extends Component {
    state={
        isedi:false
    }
    delete = async(e) =>{
        console.log(e.currentTarget.value)
            // try{
            //     let res = await post('/api/delete',{
            //         number:1
            //     })
            //     console.log(res)
            //     }
            // catch(err){
            //     console.log(err)
            // }   
        }
    render() {
        const {number,date,thing,name,student_number,status,address} = this.props
        return (
            this.state.edi ?
                <tr className="tdwrp">
                    <td><input type="checkbox" /></td>
                    <td>{number}</td>
                    <td>{date}</td>
                    <td>{thing}</td>
                    <td>{name}</td>
                    <td>{student_number}</td>
                    <td>{status}</td>
                    <td>找回日期</td>
                    <td>{address}</td>
                    <td>
                        <div>
                            <Button>编辑</Button>
                            <Button number={number} type="primary" danger onClick={this.delete}>删除</Button>                    
                        </div>
                    </td>
                </tr>:
                <tr>
                    <tr className="tdwrp">
                    <td><input type="checkbox" /></td>
                    <td>{number}</td>
                    <td>{date}</td>
                    <td>{thing}</td>
                    <td>{name}</td>
                    <td>{student_number}</td>
                    <td>{status}</td>
                    <td>找回日期</td>
                    <td>{address}</td>
                    <td>
                        <div>
                            <Button>编辑</Button>
                            <Button number={number} type="primary" danger onClick={this.delete}>删除</Button>                    
                        </div>
                    </td>
                </tr>
                </tr>
        )
    }
}
